import React, { useState, useEffect, useCallback } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

export default function ProviderDashboard() {
  const [services, setServices] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [form, setForm] = useState({ title: "", description: "", price: "" });
  const [editingId, setEditingId] = useState(null);
  const [workingHours, setWorkingHours] = useState([]);

  const getToken = () => localStorage.getItem("token");

  // Fetch services
  const fetchServices = useCallback(async () => {
    try {
      const res = await fetch("http://localhost:5000/services", {
        headers: { Authorization: "Bearer " + getToken() },
      });
      const data = await res.json();
      setServices(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
    }
  }, []);

  // Fetch appointments
  const fetchAppointments = useCallback(async () => {
    try {
      const res = await fetch("http://localhost:5000/appointments", {
        headers: { Authorization: "Bearer " + getToken() },
      });
      const data = await res.json();
      setAppointments(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
    }
  }, []);

  useEffect(() => {
    fetchServices();
    fetchAppointments();
  }, [fetchServices, fetchAppointments]);

  // Create or update service
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.title || !form.price) return alert("Title and price are required");

    try {
      if (editingId === null) {
        await fetch("http://localhost:5000/services", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + getToken(),
          },
          body: JSON.stringify(form),
        });
      } else {
        await fetch(`http://localhost:5000/services/${editingId}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + getToken(),
          },
          body: JSON.stringify(form),
        });
      }
      setForm({ title: "", description: "", price: "" });
      setEditingId(null);
      fetchServices();
      fetchAppointments();
    } catch (err) {
      console.error(err);
    }
  };

  const handleEdit = (service) => {
    setForm({ title: service.title, description: service.description, price: service.price });
    setEditingId(service.id);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this service?")) return;
    try {
      await fetch(`http://localhost:5000/services/${id}`, {
        method: "DELETE",
        headers: { Authorization: "Bearer " + getToken() },
      });
      fetchServices();
      fetchAppointments();
    } catch (err) {
      console.error(err);
    }
  };

  // --- WORKING HOURS ---
  const fetchWorkingHours = useCallback(async () => {
    try {
      const res = await fetch("http://localhost:5000/working-hours", {
        headers: { Authorization: "Bearer " + getToken() },
      });
      const data = await res.json();
      setWorkingHours(data.hours || []);
    } catch (err) {
      console.error(err);
    }
  }, []);

  useEffect(() => {
    fetchWorkingHours();
  }, [fetchWorkingHours]);

  const handleSaveWorkingHours = async () => {
    try {
      await fetch("http://localhost:5000/working-hours", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + getToken(),
        },
        body: JSON.stringify({ hours: workingHours }),
      });
      fetchWorkingHours();
      alert("Working hours saved!");
    } catch (err) {
      console.error(err);
    }
  };

  const addWorkingHour = () => {
    setWorkingHours([...workingHours, { day: "Mon", start: "09:00", end: "17:00" }]);
  };

  const updateWorkingHour = (index, field, value) => {
    const newHours = [...workingHours];
    newHours[index][field] = value;
    setWorkingHours(newHours);
  };

  const removeWorkingHour = (index) => {
    const newHours = [...workingHours];
    newHours.splice(index, 1);
    setWorkingHours(newHours);
  };

  // Convert workingHours to calendar background events
  const workingEvents = workingHours.flatMap(h => {
    const dayMap = { "Mon": 1, "Tue": 2, "Wed": 3, "Thu": 4, "Fri": 5, "Sat": 6, "Sun": 0 };
    const start = new Date();
    start.setDate(start.getDate() - start.getDay() + dayMap[h.day]);
    const [sh, sm] = h.start.split(":").map(Number);
    start.setHours(sh, sm, 0, 0);

    const end = new Date(start);
    const [eh, em] = h.end.split(":").map(Number);
    end.setHours(eh, em, 0, 0);

    return {
      start,
      end,
      display: "background",
      backgroundColor: "#27ae60",
      borderColor: "#1e8449",
    };
  });

  // Map appointments to FullCalendar events
  const calendarEvents = appointments
    .filter(a => a.status === "booked")
    .map(a => ({
      id: a.id,
      title: `${a.serviceTitle} - ${a.customerEmail}`,
      start: a.slot,
      end: new Date(new Date(a.slot).getTime() + 30 * 60 * 1000),
    }));

  const handleEventClick = async (clickInfo) => {
    if (!window.confirm("Do you want to cancel this appointment?")) return;

    try {
      await fetch(`http://localhost:5000/appointments/${clickInfo.event.id}`, {
        method: "DELETE",
        headers: { Authorization: "Bearer " + getToken() },
      });
      fetchAppointments();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ maxWidth: "900px", margin: "20px auto", padding: "20px", fontFamily: "Arial" }}>
      <h1 style={{ textAlign: "center", color: "#2c3e50" }}>Provider Dashboard</h1>

      {/* Create / Edit Service */}
      <section style={{ marginBottom: "40px" }}>
        <h2 style={{ color: "#34495e" }}>
          {editingId === null ? "Create Service" : "Edit Service"}
        </h2>
        <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
          <input
            type="text"
            placeholder="Title"
            value={form.title}
            onChange={(e) => setForm({ ...form, title: e.target.value })}
            style={{ width: "100%", padding: "10px", marginBottom: "10px", borderRadius: "5px" }}
          />
          <input
            type="text"
            placeholder="Description"
            value={form.description}
            onChange={(e) => setForm({ ...form, description: e.target.value })}
            style={{ width: "100%", padding: "10px", marginBottom: "10px", borderRadius: "5px" }}
          />
          <input
            type="number"
            placeholder="Price"
            value={form.price}
            onChange={(e) => setForm({ ...form, price: e.target.value })}
            style={{ width: "100%", padding: "10px", marginBottom: "10px", borderRadius: "5px" }}
          />
          <button
            type="submit"
            style={{
              padding: "10px 20px",
              backgroundColor: "#3498db",
              color: "#fff",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            {editingId === null ? "Create Service" : "Update Service"}
          </button>
        </form>
      </section>

      {/* Services List */}
      <section style={{ marginBottom: "40px" }}>
        <h2 style={{ color: "#34495e" }}>Services</h2>
        {services.map((s) => (
          <div
            key={s.id}
            style={{
              border: "1px solid #ccc",
              padding: "15px",
              marginBottom: "10px",
              borderRadius: "5px",
              backgroundColor: "#ecf0f1",
            }}
          >
            <h3 style={{ margin: "0 0 5px 0" }}>{s.title}</h3>
            <p style={{ margin: "0 0 5px 0" }}>{s.description}</p>
            <p style={{ margin: 0, fontWeight: "bold" }}>${s.price}</p>
            <div style={{ marginTop: "10px" }}>
              <button
                onClick={() => handleEdit(s)}
                style={{
                  marginRight: "10px",
                  padding: "5px 10px",
                  backgroundColor: "#3498db",
                  color: "#fff",
                  border: "none",
                  borderRadius: "3px",
                  cursor: "pointer",
                }}
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(s.id)}
                style={{
                  padding: "5px 10px",
                  backgroundColor: "#e74c3c",
                  color: "#fff",
                  border: "none",
                  borderRadius: "3px",
                  cursor: "pointer",
                }}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </section>

      {/* Working Hours Management */}
      <section style={{ marginBottom: "40px" }}>
        <h2 style={{ color: "#34495e" }}>Set Working Hours</h2>
        {workingHours.map((h, i) => (
          <div key={i} style={{ display: "flex", marginBottom: "5px", gap: "5px" }}>
            <select value={h.day} onChange={e => updateWorkingHour(i, "day", e.target.value)}>
              {["Mon","Tue","Wed","Thu","Fri","Sat","Sun"].map(d => <option key={d} value={d}>{d}</option>)}
            </select>
            <input type="time" value={h.start} onChange={e => updateWorkingHour(i, "start", e.target.value)} />
            <input type="time" value={h.end} onChange={e => updateWorkingHour(i, "end", e.target.value)} />
            <button type="button" onClick={() => removeWorkingHour(i)}>Remove</button>
          </div>
        ))}
        <button type="button" onClick={addWorkingHour} style={{ marginTop: "10px" }}>Add Working Hour</button>
        <button type="button" onClick={handleSaveWorkingHours} style={{ marginTop: "10px", marginLeft: "10px" }}>Save Hours</button>
      </section>

      {/* Full Calendar */}
      <section>
        <h2 style={{ color: "#34495e" }}>My Schedule</h2>
        <FullCalendar
          plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
          initialView="timeGridWeek"
          events={[...calendarEvents, ...workingEvents]}
          headerToolbar={{
            left: "prev,next today",
            center: "title",
            right: "dayGridMonth,timeGridWeek,timeGridDay",
          }}
          eventClick={handleEventClick}
          height="auto"
          slotMinTime="08:00:00"
          slotMaxTime="20:00:00"
          allDaySlot={false}
          nowIndicator={true}
          weekNumbers={true}
          dayHeaderFormat={{ weekday: 'short', day: 'numeric' }}
          slotDuration="00:30:00"
          slotLabelInterval="01:00"
          slotLabelFormat={{ hour: '2-digit', minute: '2-digit', hour12: false }}
        />
      </section>
    </div>
  );
}
