import React, { useState, useEffect, useCallback } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

export default function CustomerDashboard() {
  const [services, setServices] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [selectedServiceId, setSelectedServiceId] = useState(null);
  const [slot, setSlot] = useState("");
  const [calendarEvents, setCalendarEvents] = useState([]);

  const getToken = () => localStorage.getItem("token");

  /* ================= FETCH SERVICES ================= */
  const fetchServices = useCallback(async () => {
    try {
      const res = await fetch("http://localhost:5000/services", {
        headers: { Authorization: "Bearer " + getToken() },
      });
      const data = await res.json();
      setServices(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
    }
  }, []);

  /* ================= FETCH APPOINTMENTS ================= */
  const fetchAppointments = useCallback(async () => {
    try {
      const res = await fetch("http://localhost:5000/appointments", {
        headers: { Authorization: "Bearer " + getToken() },
      });
      const data = await res.json();
      setAppointments(
        Array.isArray(data)
          ? data.filter(a => a.status === "booked")
          : []
      );
    } catch (err) {
      console.error(err);
    }
  }, []);

  useEffect(() => {
    fetchServices();
    fetchAppointments();
  }, [fetchServices, fetchAppointments]);

  /* ================= BOOK APPOINTMENT ================= */
  const handleBookAppointment = async (e) => {
    e.preventDefault();
    if (!slot) return alert("Please enter a slot");

    const isoSlot = new Date(slot).toISOString();

    try {
      await fetch("http://localhost:5000/appointments", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + getToken(),
        },
        body: JSON.stringify({
          serviceId: Number(selectedServiceId),
          slot: isoSlot,
        }),
      });

      setSlot("");
      setSelectedServiceId(null);
      fetchAppointments();
      fetchServices();
    } catch (err) {
      console.error(err);
    }
  };

  /* ================= CANCEL APPOINTMENT ================= */
  const handleCancelAppointment = async (appointmentId) => {
    try {
      await fetch(`http://localhost:5000/appointments/${appointmentId}`, {
        method: "DELETE",
        headers: { Authorization: "Bearer " + getToken() },
      });

      setAppointments(prev => prev.filter(a => a.id !== appointmentId));
      fetchServices();
    } catch (err) {
      console.error(err);
    }
  };

  /* ================= AVAILABLE SERVICES ================= */
  const availableServices = services.filter(
    s => !appointments.some(
      a => a.serviceId === s.id && a.status === "booked"
    )
  );

  /* ================= CALENDAR ================= */
  useEffect(() => {
    if (!selectedServiceId) return;

    const service = services.find(s => s.id === selectedServiceId);
    if (!service) return;

    Promise.all([
      fetch(
        `http://localhost:5000/working-hours?providerEmail=${service.providerEmail}`,
        { headers: { Authorization: "Bearer " + getToken() } }
      ).then(res => res.json()),

      fetch(
        `http://localhost:5000/provider-appointments/${service.providerEmail}`,
        { headers: { Authorization: "Bearer " + getToken() } }
      ).then(res => res.json())
    ])
      .then(([whData, bookedData]) => {
        const dayMap = { Sun: 0, Mon: 1, Tue: 2, Wed: 3, Thu: 4, Fri: 5, Sat: 6 };

        // === WORKING HOURS (hachures) ===
        let workingEvents = [];
        const today = new Date();
        const monday = new Date(today);
        monday.setDate(today.getDate() - today.getDay() + 1); // Lundi de la semaine

        (whData.hours || []).forEach(h => {
          const dayIndex = dayMap[h.day];
          if (dayIndex === undefined) return;

          const day = new Date(monday);
          day.setDate(monday.getDate() + (dayIndex - 1));

          const [sh, sm] = h.start.split(":").map(Number);
          const [eh, em] = h.end.split(":").map(Number);

          const start = new Date(day);
          start.setHours(sh, sm, 0, 0);
          const end = new Date(day);
          end.setHours(eh, em, 0, 0);

          workingEvents.push({
            start: start.toISOString(),
            end: end.toISOString(),
            display: "background",
            backgroundColor: "#d5f5e3",
            overlap: false,
          });
        });

        // === BOOKED SLOTS ===
        const bookedEvents = bookedData.map(a => ({
          start: a.slot,
          end: new Date(new Date(a.slot).getTime() + 30 * 60 * 1000).toISOString(),
          title: "❌ Already booked",
          backgroundColor: "#f1948a",
          borderColor: "#c0392b",
        }));

        setCalendarEvents([...workingEvents, ...bookedEvents]);
      })
      .catch(console.error);

  }, [selectedServiceId, services]);

  /* ================= RENDER ================= */
  return (
    <div style={{ maxWidth: "800px", margin: "20px auto", padding: "20px", fontFamily: "Arial" }}>
      <h1 style={{ textAlign: "center", color: "#2c3e50" }}>Customer Dashboard</h1>

      {/* AVAILABLE SERVICES */}
      <section style={{ marginBottom: "40px" }}>
        <h2 style={{ color: "#34495e" }}>Available Services</h2>
        {availableServices.length > 0 ? (
          availableServices.map(s => (
            <div
              key={s.id}
              style={{
                border: "1px solid #ccc",
                padding: "15px",
                marginBottom: "10px",
                borderRadius: "5px",
                backgroundColor: "#ecf0f1",
              }}
            >
              <h3>{s.title}</h3>
              <p>{s.description}</p>
              <strong>${s.price}</strong><br />
              <button
                onClick={() => setSelectedServiceId(s.id)}
                style={{
                  marginTop: "10px",
                  padding: "8px 12px",
                  backgroundColor: "#3498db",
                  color: "#fff",
                  border: "none",
                  borderRadius: "5px",
                  cursor: "pointer",
                }}
              >
                Book Slot
              </button>
            </div>
          ))
        ) : <p>No services available.</p>}
      </section>

      {/* BOOK SLOT */}
      {selectedServiceId && (
        <section style={{ marginBottom: "40px" }}>
          <h2>Book a Slot</h2>

          <form onSubmit={handleBookAppointment} style={{ marginBottom: "20px" }}>
            <input
              type="datetime-local"
              value={slot}
              onChange={e => setSlot(e.target.value)}
              required
            />
            <button type="submit">Book Appointment</button>
          </form>

          <FullCalendar
            plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
            initialView="timeGridWeek"
            events={calendarEvents}
            height="auto"
            slotMinTime="08:00:00"
            slotMaxTime="20:00:00"
            allDaySlot={false}
            nowIndicator={true}
            slotDuration="00:30:00"
          />
        </section>
      )}

      {/* MY APPOINTMENTS */}
      <section>
        <h2>My Appointments</h2>
        {appointments.length > 0 ? (
          <ul>
            {appointments.map(a => (
              <li key={a.id}>
                {a.serviceTitle} — {a.slot}
                <button onClick={() => handleCancelAppointment(a.id)}>Cancel</button>
              </li>
            ))}
          </ul>
        ) : <p>No appointments yet.</p>}
      </section>
    </div>
  );
}
