import React, { useState, useEffect, useCallback } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

const API_URL = "/api";

export default function ProviderDashboard() {
  const [services, setServices] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [form, setForm] = useState({ title: "", description: "", price: "" });
  const [editingId, setEditingId] = useState(null);
  const [workingHours, setWorkingHours] = useState([]);
  const [newHour, setNewHour] = useState({ day: "", start: "", end: "" });

  const getToken = () => localStorage.getItem("token");

  /* ================= FETCH SERVICES ================= */
  const fetchServices = useCallback(async () => {
    try {
      const res = await fetch(`${API_URL}/services`, {
        headers: { Authorization: "Bearer " + getToken() },
      });
      if (!res.ok) throw new Error("Failed to fetch services");
      const data = await res.json();
      setServices(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("Fetch services error:", err);
    }
  }, []);

  /* ================= FETCH APPOINTMENTS ================= */
  const fetchAppointments = useCallback(async () => {
    try {
      const res = await fetch(`${API_URL}/appointments`, {
        headers: { Authorization: "Bearer " + getToken() },
      });
      if (!res.ok) throw new Error("Failed to fetch appointments");
      const data = await res.json();
      setAppointments(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("Fetch appointments error:", err);
    }
  }, []);

  useEffect(() => {
    fetchServices();
    fetchAppointments();
  }, [fetchServices, fetchAppointments]);

  /* ================= SERVICES CRUD ================= */
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.title || !form.price) return alert("Title and price are required");

    const url =
      editingId === null
        ? `${API_URL}/services`
        : `${API_URL}/services/${editingId}`;

    try {
      const res = await fetch(url, {
        method: editingId === null ? "POST" : "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + getToken(),
        },
        body: JSON.stringify(form),
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.message || "Action failed");
      }

      setForm({ title: "", description: "", price: "" });
      setEditingId(null);
      fetchServices();
    } catch (err) {
      console.error("Submit service error:", err);
      alert("Network error: " + err.message);
    }
  };

  const handleEdit = (service) => {
    setForm({ title: service.title, description: service.description, price: service.price });
    setEditingId(service.id);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this service?")) return;
    try {
      const res = await fetch(`${API_URL}/services/${id}`, {
        method: "DELETE",
        headers: { Authorization: "Bearer " + getToken() },
      });
      if (!res.ok) throw new Error("Delete failed");
      fetchServices();
    } catch (err) {
      alert("Error during deletion.");
    }
  };

  /* ================= WORKING HOURS ================= */
  const fetchWorkingHours = useCallback(async () => {
    try {
      const res = await fetch(`${API_URL}/working-hours`, {
        headers: { Authorization: "Bearer " + getToken() },
      });
      const data = await res.json();
      setWorkingHours(data.hours || []);
    } catch (err) {
      console.error(err);
    }
  }, []);

  useEffect(() => {
    fetchWorkingHours();
  }, [fetchWorkingHours]);

  const persistWorkingHours = async (hours) => {
    try {
      await fetch(`${API_URL}/working-hours`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + getToken(),
        },
        body: JSON.stringify({ hours }),
      });
    } catch (err) {
      alert("Could not save working hours.");
    }
  };

  /* ================= CALENDAR PREP ================= */
  const workingEvents = workingHours.map((h) => {
    const dayMap = { Sun: 0, Mon: 1, Tue: 2, Wed: 3, Thu: 4, Fri: 5, Sat: 6 };
    const start = new Date();
    start.setDate(start.getDate() - start.getDay() + dayMap[h.day]);
    const [sh, sm] = h.start.split(":").map(Number);
    start.setHours(sh, sm, 0, 0);
    const end = new Date(start);
    const [eh, em] = h.end.split(":").map(Number);
    end.setHours(eh, em, 0, 0);

    return { start, end, display: "background", backgroundColor: "#27ae60" };
  });

  const calendarEvents = appointments
    .filter((a) => a.status === "booked")
    .map((a) => ({
      id: a.id,
      title: `${a.serviceTitle} (${a.price} FCFA)`,
      start: a.slot,
      backgroundColor: "#3498db",
    }));

  const handleEventClick = async (info) => {
    if (!window.confirm("Cancel this appointment?")) return;
    try {
      await fetch(`${API_URL}/appointments/${info.event.id}`, {
        method: "DELETE",
        headers: { Authorization: "Bearer " + getToken() },
      });
      fetchAppointments();
    } catch (err) {
      alert("Error cancelling appointment.");
    }
  };

  return (
    <div style={{ maxWidth: "900px", margin: "20px auto", padding: "20px", fontFamily: "Arial" }}>
      <h1 style={{ textAlign: "center" }}>Provider Dashboard</h1>

      {/* FORMULAIRE */}
      <section style={{ marginBottom: "30px", padding: "20px", background: "#f9f9f9", borderRadius: "8px" }}>
        <h2>{editingId ? "Edit Service" : "Create Service"}</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" placeholder="Title" value={form.title} 
            onChange={e => setForm({ ...form, title: e.target.value })} 
            style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />
          <input type="number" placeholder="Price" value={form.price} 
            onChange={e => setForm({ ...form, price: e.target.value })} 
            style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />
          <button type="submit" style={{ padding: "10px 20px", background: "#3498db", color: "#fff", border: "none" }}>
            {editingId ? "Update" : "Create"}
          </button>
          {editingId && <button onClick={() => {setEditingId(null); setForm({title:"", description:"", price:""})}} style={{marginLeft: "10px"}}>Cancel</button>}
        </form>
      </section>

      {/* LISTE SERVICES */}
      <section style={{ marginBottom: "30px" }}>
        {services.map(s => (
          <div key={s.id} style={{ border: "1px solid #ddd", padding: "10px", marginBottom: "10px" }}>
            <strong>{s.title}</strong> - {s.price} FCFA
            <div>
              <button onClick={() => handleEdit(s)}>Edit</button>
              <button onClick={() => handleDelete(s.id)} style={{ color: "red" }}>Delete</button>
            </div>
          </div>
        ))}
      </section>

      {/* CALENDRIER */}
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="timeGridWeek"
        events={[...calendarEvents, ...workingEvents]}
        eventClick={handleEventClick}
        height="auto"
      />
    </div>
  );
}
