import React, { useState, useEffect, useCallback } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

export default function CustomerDashboard() {
  const [services, setServices] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [selectedServiceId, setSelectedServiceId] = useState(null);
  const [slot, setSlot] = useState("");
  const [calendarEvents, setCalendarEvents] = useState([]);

  const getToken = () => localStorage.getItem("token");

  const fetchServices = useCallback(async () => {
    try {
      const res = await fetch("http://localhost:5000/services", {
        headers: { Authorization: "Bearer " + getToken() },
      });
      const data = await res.json();
      setServices(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
    }
  }, []);

  const fetchAppointments = useCallback(async () => {
    try {
      const res = await fetch("http://localhost:5000/appointments", {
        headers: { Authorization: "Bearer " + getToken() },
      });
      const data = await res.json();
      setAppointments(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
    }
  }, []);

  useEffect(() => {
    fetchServices();
    fetchAppointments();
  }, [fetchServices, fetchAppointments]);

  // Book an appointment
  const handleBookAppointment = async (e) => {
    e.preventDefault();
    if (!slot) return alert("Please enter a slot");

    const isoSlot = new Date(slot).toISOString();

    try {
      await fetch("http://localhost:5000/appointments", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + getToken(),
        },
        body: JSON.stringify({
          serviceId: Number(selectedServiceId),
          slot: isoSlot,
        }),
      });
      setSlot("");
      setSelectedServiceId(null);
      fetchAppointments();
      fetchServices();
    } catch (err) {
      console.error(err);
    }
  };

  // ✅ DELETE appointment (permanent)
  const handleDeleteAppointment = async (appointmentId) => {
    if (!window.confirm("Delete this appointment permanently?")) return;

    try {
      await fetch(`http://localhost:5000/appointments/${appointmentId}`, {
        method: "DELETE",
        headers: {
          Authorization: "Bearer " + getToken(),
        },
      });

      // ✅ UPDATE STATE IMMEDIATELY
      setAppointments((prev) =>
        prev.filter((a) => a.id !== appointmentId)
      );
    } catch (err) {
      console.error(err);
    }
  };

  // ✅ CANCEL appointment (status change)
  const handleCancelAppointment = async (appointmentId) => {
    if (!window.confirm("Cancel this appointment?")) return;

    try {
      await fetch(
        `http://localhost:5000/appointments/${appointmentId}/cancel`,
        {
          method: "PUT",
          headers: {
            Authorization: "Bearer " + getToken(),
          },
        }
      );

      // ✅ UPDATE STATE IMMEDIATELY
      setAppointments((prev) =>
        prev.filter((a) => a.id !== appointmentId)
      );
    } catch (err) {
      console.error(err);
    }
  };

  const availableServices = services.filter(
    (s) => !appointments.some((a) => a.serviceId === s.id)
  );

  useEffect(() => {
    if (!selectedServiceId) return;
    const service = services.find((s) => s.id === selectedServiceId);
    if (!service) return;

    fetch(
      `http://localhost:5000/working-hours?providerEmail=${service.providerEmail}`,
      {
        headers: { Authorization: "Bearer " + getToken() },
      }
    )
      .then((res) => res.json())
      .then((data) => {
        const dayMap = { Sun: 0, Mon: 1, Tue: 2, Wed: 3, Thu: 4, Fri: 5, Sat: 6 };

        const workingEvents = (data.hours || []).map((h) => {
          const start = new Date();
          start.setDate(start.getDate() - start.getDay() + dayMap[h.day]);
          const [sh, sm] = h.start.split(":").map(Number);
          start.setHours(sh, sm, 0, 0);

          const end = new Date(start);
          const [eh, em] = h.end.split(":").map(Number);
          end.setHours(eh, em, 0, 0);

          return {
            start,
            end,
            display: "background",
            backgroundColor: "#d0d0d0",
          };
        });

        const bookedAppointments = appointments
          .filter((a) => a.status === "booked")
          .map((a) => ({
            start: a.slot,
            end: new Date(new Date(a.slot).getTime() + 30 * 60 * 1000),
            title: a.serviceTitle,
            backgroundColor: "#3498db",
          }));

        setCalendarEvents([...workingEvents, ...bookedAppointments]);
      })
      .catch(console.error);
  }, [selectedServiceId, services, appointments]);

  return (
    <div style={{ maxWidth: "800px", margin: "20px auto" }}>
      <h1>Customer Dashboard</h1>

      <section>
        <h2>Available Services</h2>
        {availableServices.map((s) => (
          <div key={s.id}>
            <h3>{s.title}</h3>
            <p>{s.description}</p>
            <button onClick={() => setSelectedServiceId(s.id)}>
              Book Slot
            </button>
          </div>
        ))}
      </section>

      {selectedServiceId && (
        <form onSubmit={handleBookAppointment}>
          <input
            type="datetime-local"
            value={slot}
            onChange={(e) => setSlot(e.target.value)}
            required
          />
          <button type="submit">Book</button>
        </form>
      )}

      <section>
        <h2>My Appointments</h2>
        {appointments.map((a) => (
          <div key={a.id} style={{ border: "1px solid #ccc", margin: "10px" }}>
            <p><strong>Service:</strong> {a.serviceTitle}</p>
            <p><strong>Slot:</strong> {a.slot}</p>

            <button
              onClick={() => handleCancelAppointment(a.id)}
              style={{ marginRight: "10px" }}
            >
              Cancel
            </button>

            <button
              onClick={() => handleDeleteAppointment(a.id)}
              style={{ background: "red", color: "white" }}
            >
              Delete
            </button>
          </div>
        ))}
      </section>

      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="timeGridWeek"
        events={calendarEvents}
      />
    </div>
  );
}
