import React, { useState, useEffect, useCallback } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

export default function CustomerDashboard() {
  const [services, setServices] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [selectedServiceId, setSelectedServiceId] = useState(null);
  const [slot, setSlot] = useState("");
  const [calendarEvents, setCalendarEvents] = useState([]);

  const getToken = () => localStorage.getItem("token");

  /* ================= FETCH SERVICES ================= */
  const fetchServices = useCallback(async () => {
    try {
      const res = await fetch("http://localhost:5000/services", {
        headers: { Authorization: "Bearer " + getToken() },
      });
      const data = await res.json();
      setServices(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
    }
  }, []);

  /* ================= FETCH APPOINTMENTS ================= */
  const fetchAppointments = useCallback(async () => {
    try {
      const res = await fetch("http://localhost:5000/appointments", {
        headers: { Authorization: "Bearer " + getToken() },
      });
      const data = await res.json();

      // ✅ PROTECTION: on garde uniquement les appointments booked
      setAppointments(
        Array.isArray(data)
          ? data.filter(a => a.status === "booked")
          : []
      );
    } catch (err) {
      console.error(err);
    }
  }, []);

  useEffect(() => {
    fetchServices();
    fetchAppointments();
  }, [fetchServices, fetchAppointments]);

  /* ================= BOOK APPOINTMENT ================= */
  const handleBookAppointment = async (e) => {
    e.preventDefault();
    if (!slot) return alert("Please enter a slot");

    const isoSlot = new Date(slot).toISOString();

    try {
      await fetch("http://localhost:5000/appointments", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + getToken(),
        },
        body: JSON.stringify({
          serviceId: Number(selectedServiceId),
          slot: isoSlot,
        }),
      });

      setSlot("");
      setSelectedServiceId(null);

      // refresh propre
      fetchAppointments();
      fetchServices();
    } catch (err) {
      console.error(err);
    }
  };

  /* ================= CANCEL APPOINTMENT ================= */
  const handleCancelAppointment = async (appointmentId) => {
    try {
      await fetch(`http://localhost:5000/appointments/${appointmentId}`, {
        method: "DELETE",
        headers: { Authorization: "Bearer " + getToken() },
      });

      // ✅ suppression locale définitive
      setAppointments(prev =>
        prev.filter(a => a.id !== appointmentId)
      );

      fetchServices();
    } catch (err) {
      console.error(err);
    }
  };

  /* ================= AVAILABLE SERVICES ================= */
  const availableServices = services.filter(
    s => !appointments.some(
      a => a.serviceId === s.id && a.status === "booked"
    )
  );

  /* ================= CALENDAR ================= */
  useEffect(() => {
    if (!selectedServiceId) return;
    const service = services.find(s => s.id === selectedServiceId);
    if (!service) return;

    fetch(`http://localhost:5000/working-hours?providerEmail=${service.providerEmail}`, {
      headers: { Authorization: "Bearer " + getToken() },
    })
      .then(res => res.json())
      .then(data => {
        const dayMap = { Sun: 0, Mon: 1, Tue: 2, Wed: 3, Thu: 4, Fri: 5, Sat: 6 };

        // ✅ Normalisation des working hours
        const backendHours = data.hours || [];
        const workingEvents = backendHours.map(h => {
          let dayKey = h.day;
          if (typeof h.day === "string") {
            dayKey = h.day.slice(0,3).charAt(0).toUpperCase() + h.day.slice(1,3).toLowerCase();
          }

          const start = new Date();
          start.setDate(start.getDate() - start.getDay() + (dayMap[dayKey] ?? 0));

          let [sh, sm] = ["00", "00"];
          if (h.start) [sh, sm] = h.start.split(":").map(Number);
          start.setHours(sh, sm, 0, 0);

          const end = new Date(start);
          let [eh, em] = ["00", "00"];
          if (h.end) [eh, em] = h.end.split(":").map(Number);
          end.setHours(eh, em, 0, 0);

          return {
            start,
            end,
            display: "background",
            allDay: false,
            // ✅ Hachuré pour montrer que le customer ne doit pas book
            backgroundColor: "#ecf0f1",
            borderColor: "#bdc3c7",
            overlap: false,
          };
        });

        // Appointments déjà bookés
        const bookedAppointments = appointments.map(a => ({
          start: a.slot,
          end: new Date(new Date(a.slot).getTime() + 30 * 60 * 1000),
          title: a.serviceTitle,
          backgroundColor: "#3498db",
          borderColor: "#2980b9",
        }));

        setCalendarEvents([...workingEvents, ...bookedAppointments]);
      })
      .catch(console.error);
  }, [selectedServiceId, services, appointments]);

  /* ================= RENDER ================= */
  return (
    <div style={{ maxWidth: "800px", margin: "20px auto", padding: "20px", fontFamily: "Arial" }}>
      <h1 style={{ textAlign: "center", color: "#2c3e50" }}>Customer Dashboard</h1>

      {/* AVAILABLE SERVICES */}
      <section style={{ marginBottom: "40px" }}>
        <h2 style={{ color: "#34495e" }}>Available Services</h2>
        {availableServices.length > 0 ? (
          availableServices.map(s => (
            <div
              key={s.id}
              style={{
                border: "1px solid #ccc",
                padding: "15px",
                marginBottom: "10px",
                borderRadius: "5px",
                backgroundColor: "#ecf0f1",
              }}
            >
              <h3 style={{ margin: "0 0 5px 0", color: "#2c3e50" }}>{s.title}</h3>
              <p style={{ margin: "0 0 5px 0", color: "#7f8c8d" }}>{s.description}</p>
              <strong style={{ color: "#16a085" }}>${s.price}</strong><br />
              <button
                onClick={() => setSelectedServiceId(s.id)}
                style={{
                  marginTop: "10px",
                  padding: "8px 12px",
                  backgroundColor: "#3498db",
                  color: "#fff",
                  border: "none",
                  borderRadius: "5px",
                  cursor: "pointer",
                }}
              >
                Book Slot
              </button>
            </div>
          ))
        ) : <p>No services available.</p>}
      </section>

      {/* BOOK SLOT */}
      {selectedServiceId && (
        <section style={{ marginBottom: "40px" }}>
          <h2 style={{ color: "#34495e" }}>Book a Slot</h2>
          <form onSubmit={handleBookAppointment} style={{ marginBottom: "20px" }}>
            <input
              type="datetime-local"
              value={slot}
              onChange={e => setSlot(e.target.value)}
              required
              style={{ padding: "8px", borderRadius: "5px", marginRight: "10px" }}
            />
            <button
              type="submit"
              style={{
                padding: "8px 12px",
                backgroundColor: "#27ae60",
                color: "#fff",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Book Appointment
            </button>
          </form>

          <FullCalendar
            plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
            initialView="timeGridWeek"
            events={calendarEvents}
            height="auto"
            slotMinTime="08:00:00"
            slotMaxTime="20:00:00"
            allDaySlot={false}
            nowIndicator={true}
            slotDuration="00:30:00"
            slotLabelInterval="01:00"
            slotLabelFormat={{ hour: "2-digit", minute: "2-digit", hour12: false }}
          />
        </section>
      )}

      {/* MY APPOINTMENTS */}
      <section>
        <h2 style={{ color: "#34495e" }}>My Appointments</h2>
        {appointments.length > 0 ? (
          <ul style={{ listStyle: "none", padding: 0 }}>
            {appointments.map(a => (
              <li
                key={a.id}
                style={{
                  border: "1px solid #ccc",
                  padding: "10px",
                  marginBottom: "10px",
                  borderRadius: "5px",
                  backgroundColor: "#ecf0f1",
                }}
              >
                <strong>Service:</strong> {a.serviceTitle}<br />
                <strong>Slot:</strong> {a.slot}<br />
                <button
                  onClick={() => handleCancelAppointment(a.id)}
                  style={{
                    marginTop: "5px",
                    padding: "5px 10px",
                    backgroundColor: "#e74c3c",
                    color: "#fff",
                    border: "none",
                    borderRadius: "3px",
                    cursor: "pointer",
                  }}
                >
                  Cancel
                </button>
              </li>
            ))}
          </ul>
        ) : <p>No appointments yet.</p>}
      </section>
    </div>
  );
}
