import React, { useState, useEffect, useCallback } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

export default function ProviderDashboard() {
  const [services, setServices] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [form, setForm] = useState({ title: "", description: "", price: "" });
  const [editingId, setEditingId] = useState(null);
  const [workingHours, setWorkingHours] = useState([]);

  const getToken = () => localStorage.getItem("token");

  /* ================= FETCH SERVICES ================= */
  const fetchServices = useCallback(async () => {
    const res = await fetch("http://localhost:5000/services", {
      headers: { Authorization: "Bearer " + getToken() },
    });
    const data = await res.json();
    setServices(Array.isArray(data) ? data : []);
  }, []);

  /* ================= FETCH APPOINTMENTS ================= */
  const fetchAppointments = useCallback(async () => {
    const res = await fetch("http://localhost:5000/appointments", {
      headers: { Authorization: "Bearer " + getToken() },
    });
    const data = await res.json();
    setAppointments(Array.isArray(data) ? data : []);
  }, []);

  useEffect(() => {
    fetchServices();
    fetchAppointments();
  }, [fetchServices, fetchAppointments]);

  /* ================= SERVICES CRUD ================= */
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.title || !form.price) return alert("Title and price are required");

    const url =
      editingId === null
        ? "http://localhost:5000/services"
        : `http://localhost:5000/services/${editingId}`;

    await fetch(url, {
      method: editingId === null ? "POST" : "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + getToken(),
      },
      body: JSON.stringify(form),
    });

    setForm({ title: "", description: "", price: "" });
    setEditingId(null);
    fetchServices();
    fetchAppointments();
  };

  const handleEdit = (service) => {
    setForm(service);
    setEditingId(service.id);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this service?")) return;
    await fetch(`http://localhost:5000/services/${id}`, {
      method: "DELETE",
      headers: { Authorization: "Bearer " + getToken() },
    });
    fetchServices();
    fetchAppointments();
  };

  /* ================= WORKING HOURS ================= */
  const fetchWorkingHours = useCallback(async () => {
    const res = await fetch("http://localhost:5000/working-hours", {
      headers: { Authorization: "Bearer " + getToken() },
    });
    const data = await res.json();
    setWorkingHours(data.hours || []);
  }, []);

  useEffect(() => {
    fetchWorkingHours();
  }, [fetchWorkingHours]);

  const handleSaveWorkingHours = async () => {
    await fetch("http://localhost:5000/working-hours", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + getToken(),
      },
      body: JSON.stringify({ hours: workingHours }),
    });
    fetchWorkingHours();
  };

  /* ================= CALENDAR EVENTS ================= */
  const workingEvents = workingHours.map((h) => {
    const dayMap = { Sun: 0, Mon: 1, Tue: 2, Wed: 3, Thu: 4, Fri: 5, Sat: 6 };
    const start = new Date();
    start.setDate(start.getDate() - start.getDay() + dayMap[h.day]);
    const [sh, sm] = h.start.split(":").map(Number);
    start.setHours(sh, sm, 0, 0);

    const end = new Date(start);
    const [eh, em] = h.end.split(":").map(Number);
    end.setHours(eh, em, 0, 0);

    return {
      start,
      end,
      display: "background",
      backgroundColor: "#27ae60",
      borderColor: "#1e8449",
    };
  });

  const calendarEvents = appointments
    .filter((a) => a.status === "booked")
    .map((a) => ({
      id: a.id,
      title: `${a.serviceTitle} - ${a.customerEmail}`,
      start: a.slot,
      end: new Date(new Date(a.slot).getTime() + 30 * 60 * 1000),
    }));

  const handleEventClick = async (info) => {
    if (!window.confirm("Cancel this appointment?")) return;
    await fetch(`http://localhost:5000/appointments/${info.event.id}`, {
      method: "DELETE",
      headers: { Authorization: "Bearer " + getToken() },
    });
    fetchAppointments();
  };

  /* ================= RENDER ================= */
  return (
    <div style={{ maxWidth: 900, margin: "20px auto", fontFamily: "Arial" }}>
      <h1>Provider Dashboard</h1>

      <form onSubmit={handleSubmit}>
        <input placeholder="Title" value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })} />
        <input placeholder="Description" value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })} />
        <input type="number" placeholder="Price" value={form.price}
          onChange={(e) => setForm({ ...form, price: e.target.value })} />
        <button type="submit">
          {editingId === null ? "Create" : "Update"} Service
        </button>
      </form>

      <h2>Services</h2>
      {services.map((s) => (
        <div key={s.id}>
          <strong>{s.title}</strong> (${s.price})
          <button onClick={() => handleEdit(s)}>Edit</button>
          <button onClick={() => handleDelete(s.id)}>Delete</button>
        </div>
      ))}

      {/* âœ… UTILISATION DE handleSaveWorkingHours */}
      <button
        onClick={handleSaveWorkingHours}
        style={{ margin: "20px 0", padding: "10px" }}
      >
        Save Working Hours
      </button>

      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="timeGridWeek"
        events={[...calendarEvents, ...workingEvents]}
        eventClick={handleEventClick}
        height="auto"
      />
    </div>
  );
}
